import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import './UpdateEmployee.css';

function UpdateEmployee() {
    const { id } = useParams();
    const [form, setForm] = useState({ name: '', email: '', position: '', salary: '', experience: '' });
    const [error, setError] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        fetch(`http://localhost:8000/employees/${id}`)
            .then(res => res.json())
            .then(data => setForm(data));
    }, [id]);

    const handleChange = (e) => {
        setForm({ ...form, [e.target.name]: e.target.value });
        setError('');
    };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const res = await fetch(`http://localhost:8000/employees/${id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(form)
    });

    if (res.status === 400) {
      setError('Email already exists!');
    } else {
      navigate('/');
    }
  };

  return (
    <div className='update-container'>
      <h2>Update Employee</h2>
      {error && <p className="error-message">{error}</p>}
      <form onSubmit={handleSubmit}>
        <input name="name" value={form.name} onChange={handleChange} required /><br />
        <input name="email" value={form.email} onChange={handleChange} required /><br />
        <input name="position" value={form.position} onChange={handleChange} required /><br />
        <input name="salary" value={form.salary} onChange={handleChange} /><br />
        <input name="experience" value={form.experience} onChange={handleChange} /><br />
        <button type="submit">Update</button>
      </form>
    </div>
  );
}

export default UpdateEmployee;
