import React, { useEffect, useState } from "react";
import axios from "axios";
import Employee from "./Employee";
import "./EmployeeList.css";

const EmployeeList = () => {
  const [employees, setEmployees] = useState([]);
  const [filtered, setFiltered] = useState([]);
  const [positions, setPositions] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [showFilter, setShowFilter] = useState(false);
  const [filterOptions, setFilterOptions] = useState({
    selectedPositions: [],
    expMin: "",
    expMax: "",
  });

  const fetchEmployees = async () => {
    try {
      const res = await axios.get("http://localhost:8000/employees");
      setEmployees(res.data);
      setFiltered(res.data);
      setPositions([...new Set(res.data.map(e => e.position))].sort());
    } catch (err) {
      console.error(`Error fetching employees: ${err}`);
    }
  };

  useEffect(() => {
    fetchEmployees();
  }, []);

  const applyFilters = () => {
    const term = searchTerm.toLowerCase().trim();
    let arr = employees.filter(emp =>
      emp.name.toLowerCase().includes(term)
    );

    const { selectedPositions, expMin, expMax } = filterOptions;

    if (selectedPositions.length) {
      arr = arr.filter(emp =>
        selectedPositions.includes(emp.position)
      );
    }

    if (expMin) {
      arr = arr.filter(emp => +emp.experience >= +expMin);
    }

    if (expMax) {
      arr = arr.filter(emp => +emp.experience <= +expMax);
    }

    setFiltered(arr);
    setShowFilter(false);
  };

  const resetFilters = () => {
    setFilterOptions({ selectedPositions: [], expMin: "", expMax: "" });
    setFiltered(employees.filter(emp =>
      emp.name.toLowerCase().includes(searchTerm.toLowerCase())
    ));
    setShowFilter(false);
  };

  return (
    <>
      <div className="search-filter-row">
        <input
          className="search-input"
          type="text"
          placeholder="Search by name..."
          value={searchTerm}
          onChange={(e) => {
            setSearchTerm(e.target.value);
            // Live update search
            setFiltered(employees.filter(emp =>
              emp.name.toLowerCase().includes(e.target.value.toLowerCase())
            ));
          }}
        />
        <button
          className="btn btn-filter"
          onClick={() => setShowFilter(!showFilter)}
        >
          Filters
        </button>
      </div>

      {showFilter && (
        <div className="filter-panel">
          <div className="filter-row">
            <label>Position:</label>
            <select
              multiple
              value={filterOptions.selectedPositions}
              onChange={(e) => {
                const sel = Array.from(e.target.selectedOptions, o => o.value);
                setFilterOptions({ ...filterOptions, selectedPositions: sel });
              }}
            >
              {positions.map((pos) => (
                <option key={pos} value={pos}>{pos}</option>
              ))}
            </select>
          </div>
          <div className="filter-row">
            <label>Experience:</label>
            <input
              type="number"
              placeholder="Min"
              value={filterOptions.expMin}
              onChange={(e) => setFilterOptions({ ...filterOptions, expMin: e.target.value })}
            />
            <input
              type="number"
              placeholder="Max"
              value={filterOptions.expMax}
              onChange={(e) => setFilterOptions({ ...filterOptions, expMax: e.target.value })}
            />
          </div>
          <div className="filter-actions">
            <button className="btn small-btn" onClick={resetFilters}>Reset</button>
            <button className="btn small-btn" onClick={applyFilters}>Apply</button>
          </div>
        </div>
      )}

      <div className="employee-table-wrapper">
        <table className="employee-table">
          <thead>
            <tr>
              <th>Name</th>
              <th>Position</th>
              <th>Experience</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {filtered.map(emp => (
              <Employee key={emp.id} emp={emp} onDelete={applyFilters} />
            ))}
          </tbody>
        </table>
      </div>
    </>
  );
};

export default EmployeeList;
