import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";
import "./ProfilePage.css";

const ProfilePage = () => {
  const { id } = useParams();
  const [employee, setEmployee] = useState(null);
  const navigate = useNavigate();

  const fetchEmployee = async () => {
    try {
      const res = await axios.get(`http://localhost:8000/employees/${id}`);
      setEmployee(res.data);
    } catch (error) {
      console.error("Error fetching employee profile:", error);
    }
  };

  useEffect(() => {
    fetchEmployee();
  }, [id]);

  if (!employee) return <div className="loading">Loading...</div>;

  return (
    <div className="profile-container">
      <button className="back-button" onClick={() => navigate("/")}>
        ← Back to Home
      </button>

      <div className="profile-card">
        {employee.image && (
          <img
            src={`data:image/jpeg;base64,${employee.image}`}
            alt="Employee"
            className="profile-image"
          />
        )}
        <div className="profile-details">
          <h2 className="employee-name">{employee.name}</h2>
          <p><strong>Email:</strong> {employee.email}</p>
          <p><strong>Phone:</strong> {employee.phonenum}</p>
          <p><strong>Position:</strong> {employee.position}</p>
          <p><strong>Experience:</strong> {employee.experience}</p>
          <p><strong>Salary:</strong> ₹{employee.salary}</p>
        </div>
      </div>
    </div>
  );
};

export default ProfilePage;
