import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";

const ProfilePage = () => {
  const { id } = useParams();
  const [employee, setEmployee] = useState(null);
  const navigate = useNavigate();

  const fetchEmployee = async () => {
    try {
      const res = await axios.get(`http://localhost:8000/employees/${id}`);
      setEmployee(res.data);
    } catch (error) {
      console.error("Error fetching employee profile:", error);
    }
  };

  useEffect(() => {
    fetchEmployee();
  }, [id]);

  if (!employee) return <div>Loading...</div>;

  return (
    <div className="container">
      <button className="btn btn-secondary mt-3 mb-3" onClick={() => navigate("/")}>
        Back to Home
      </button>

      <div className="card" style={{ maxWidth: "500px", margin: "0 auto" }}>
        {employee.image && (
          <img
            src={`data:image/jpeg;base64,${employee.image}`}
            alt="Employee"
            className="card-img-top"
            style={{ maxHeight: "300px", objectFit: "cover" }}
          />
        )}
        <div className="card-body">
          <h4 className="card-title">{employee.name}</h4>
          <p className="card-text"><strong>Email:</strong> {employee.email}</p>
          <p className="card-text"><strong>Phone:</strong> {employee.phonenum}</p>
          <p className="card-text"><strong>Position:</strong> {employee.position}</p>
          <p className="card-text"><strong>Experience:</strong> {employee.experience}</p>
          <p className="card-text"><strong>Salary:</strong> â‚¹{employee.salary}</p>
        </div>
      </div>
    </div>
  );
};

export default ProfilePage;
