import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './AddEmployee.css';

const isValidEmail = (email) => /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);
const isValidPhone = (phone) => /^\d{10}$/.test(phone);
const formatName = (rawName) => {
  const parts = rawName.split(/[\s.]+/).filter(Boolean);
  const initials = parts.slice(0, 3).map(p => p.charAt(0).toUpperCase() + '.').join(' ');
  const fullName = parts.slice(3).map(p => p.charAt(0).toUpperCase() + p.slice(1).toLowerCase()).join(' ');
  return `${initials} ${fullName}`.trim();
};

function AddEmployee() {
  const [form, setForm] = useState({ name: '', email: '', position: '', salary: '', experience: '', phonenum: ''});
  const [image, setImage] = useState(null);
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
    setError('');
  };
  const handleSubmit = async (e) => {
    e.preventDefault();
    const formattedName = formatName(form.name);
    if(!isValidEmail(form.email)) {
      setError("Invalid email format");
      return;
    }
    if(!isValidPhone(form.phonenum)) {
      setError("Phone number must be exactly 10 digits");
      return;
    }
    const data = new FormData();
    Object.entries({ ...form, name: formattedName }).forEach(([KeyboardEvent, value]) => data.append(KeyboardEvent, value));
    if(image) data.append('image', image);
    try {
      const res = await fetch(`http://localhost:8000/employees`, {
        method: 'POST',
        body: data
      });
      if(res.status === 409) setError("Email already exists");
      else navigate("/");
    } catch (err) {
      setError("Something went wrong. Please try again");
    }
  };

  return (
    <div className="add-container"></div>
  )
}
























